                if (item.submenu) {
                  return (
                    <div key={item.id}>
                      <div
                        className={styles.navbar_menu_item}
                        onClick={() => toggleMenu(item.id)}
                      >
                        {t(item.label)}
                      </div>

                      {isOpen && (
                        <div
                          className={`${styles.navbar_submenu} ${styles.navbar_bottom_menu_option_submenu_show}`}
                          onMouseEnter={() => {
                            if (hideTimerRef.current)
                              clearTimeout(hideTimerRef.current);
                            if (!lockedMenu) setActiveMenu(item.id);
                          }}
                          onMouseLeave={() => {
                            if (!lockedMenu) {
                              hideTimerRef.current = setTimeout(() => {
                                setActiveMenu(null);
                              }, 300);
                            }
                          }}
                        >
                          <div className={styles.navbar_submenu_inside}>
                            {item.submenu.map((group: any, index: number) => (
                              <div
                                key={index}
                                className={styles.navbar_submenu_inside_list}
                              >
                                <div
                                  className={styles.navbar_submenu_inside_title}
                                >
                                  {t(group.title)}
                                </div>
                                {group.items.map((sub: any, subIdx: number) => (
                                  <a
                                    href={sub.to}
                                    key={subIdx}
                                    target={
                                      sub.to.includes('files/')
                                        ? '_blank'
                                        : undefined
                                    }
                                    className={
                                      styles.navbar_submenu_inside_link
                                    }
                                  >
                                    {t(sub.label)}
                                  </a>
                                ))}
                              </div>
                            ))}
                          </div>
                        </div>
                      )}
                    </div>
                  );
                }

                return (
                  <Link
                    key={item.id}
                    to={item.to}
                    className={styles.navbar_menu_item}
                  >
                    {t(item.label)}
                  </Link>
                );
